const css = `/* BEGIN V2 LOADER */
/* =============== */

#gd-loading-icon {
  background-image: url(data:image/svg+xml;base64,);
}
#gd-loading-icon {
  position: fixed;
  bottom:5px;
  right:5px;
  z-index: 2147483647;
  display: block;
  width: 20px;
  height: 20px;
  background-size: 100% 100%;
  animation: gd-loading-animation 1.5s ease-in-out infinite;
}

@keyframes gd-loading-animation {
  0% { opacity: 0.05; }
  50% { opacity: 0.6; }
  100% { opacity: 0.05; }
}
/* =============== */
/*  END V2 LOADER  */`;

const iconStyle = document.createElement("style");
iconStyle.textContent = css;

const loadingIcon = document.createElement("div");
loadingIcon.id = "gd-loading-icon";
loadingIcon.className = "gd-loaderv2";
loadingIcon.title = "GreaterDiscord is loading...";

export default class {
    static show() {
        document.body.appendChild(iconStyle);
        document.body.appendChild(loadingIcon);
    }

    static hide() {
        if (iconStyle) iconStyle.remove();
        if (loadingIcon) loadingIcon.remove();
    }
}